	rep(i, N) cin >> a[i + 1];

	int ans = 0;
	for(int i=1; i<=N; i++){
		if (dp[i] == -1) {
			dp[i] = solve(i);
		}
		ans = max(ans, dp[i]);
	}

	cout << ans << endl;

	return 0;
}

