int time(int i)
{
	if (~dp[i])
		return dp[i];

	int late = 0;
	if (len[i] <= len[i - 1])
		late = time(i - 1);
	if (len[i] <= len[i + 1])
		max_swap(late, time(i + 1));

	return dp[i] = (L - len[i]) + late;
}
