int dfs (int curr )
{
	int res = max (0, l - icicles[curr] );

	// curr のつららが一番大きい
	if (icicles[curr-1] < icicles[curr] && icicles[curr] > icicles[curr+1] ){
		return res;
	}else	// 前のつららが大きく、後ろのつららが小さい
	if (icicles[curr-1] > icicles[curr] && icicles[curr] > icicles[curr+1] ){
		return res + dfs (curr-1);
	}else	// 前のつららが小さく、後ろのつららが大きい
	if (icicles[curr-1] < icicles[curr] && icicles[curr] < icicles[curr+1] ){
		return res + dfs (curr+1);
	}else{	// curr のつららが一番小さい
		return res + max (dfs (curr-1), dfs (curr+1) );
