#include <queue>
using namespace std;

const int MAX_N = 100001;
int N, L;
int A[MAX_N];
int T[MAX_N];

int rec(int p) {
  int &res = T[p];
  if(res == -1) {
    int wait = 0;
    for(int d = -1; d <= 1; d += 2) {
      int np = p + d;
      if(np < 0 || np >= N) continue;
      if(A[p] < A[np]) wait = max(wait,rec(np));
    }
    res = wait + L - A[p];
  }
  return res;
}

int solve() {
  int ans = 0;
  fill(T,T+N,-1);
  for(int i = 0; i < N; ++i) ans = max(ans, rec(i));
  return ans;
}

int main() {
  cin >> N >> L;
  for(int i = 0; i < N; ++i) cin >> A[i];
  cout << solve() << endl;
  return 0;
}

