int n, l;
int icicles[100005];
ll memo[100005];

ll life (int i )
{
	if (memo[i] != -1LL ) return memo[i];

	ll res = (ll)(l - icicles[i]);
	// i 番目のつららが一番大きい
	if (icicles[i-1] < icicles[i] && icicles[i] > icicles[i+1] ){
		return memo[i] = res;
	}else	// 前のつららが大きく、後ろのつららが小さい
	if (icicles[i-1] > icicles[i] && icicles[i] > icicles[i+1] ){
		return memo[i] = (life (i-1) + res );
	}else	// 前のつららが小さく、後ろのつららが大きい
	if (icicles[i-1] < icicles[i] && icicles[i] < icicles[i+1] ){
		return memo[i] = (life (i+1) + res );
	}else{	// i 番目のつららが一番小さい
		return memo[i] = (max (life (i-1), life (i+1) ) + res );
	} // end if
}

