			scanf ("%d", &icicles[i] );
		} // end rep

		ll res = 0LL;
		for (int i = 1; i <= n; i++ ){
			ll curr = life (i );
			res = max (res, curr );
		} // end for

		printf ("%ld\n", res );
	} // end loop

	return 0;
}

