		if((a[i]-a[i-1]) * (a[i+1]-a[i]) <= 0 || i == N){
			Max = max(Max, num*L - sum);
			num = 1;
			sum = a[i];
		}
	}

	cout << Max << endl;
	while(1);

	return 0;
}

