typedef long long ll;
typedef pair<ll,int> P;
const ll INF=1000000000000LL;

int n,l;
ll a[100001];
bool used[100001];
priority_queue<P> pq;

void neighborSearch(int left,ll diff){
    if(!used[left]&&(left==0||a[left]>a[left-1])&&(left==n-1||a[left]>a[left+1])){
        used[left]=true;
        pq.push(P(a[left]-diff,left));
    }
}

int main(){
    cin>>n>>l;
    fill(used,used+100001,false);
    for(int i = 0; i < n; i++)
        cin>>a[i];
    for(int i = 0; i < n; i++)
        neighborSearch(i,0);
    ll diff=0;
    while(pq.size()){
        ll length=0;
        vector<int> nums;
        while(pq.size()){
            if(length==0||length==pq.top().first){
                if(length==0)
                    diff+=l-pq.top().first;
                l=pq.top().first;
                length=l;
                nums.push_back(pq.top().second);
                a[pq.top().second]=-INF;
                pq.pop();
            }
            else
                break;
        }
        for(int i = 0; i < nums.size(); i++){
            int left=nums[i]-1;
            int right=nums[i]+1;
            if(left>=0)
                neighborSearch(left,diff);
            if(right<n)
                neighborSearch(right,diff);
        }
    }
    cout<<diff<<endl;
    return 0;
}

