const int MAX = 1e5+1;

int T[MAX];
int N,L;
vector<int> V;

int rec(int n){
  // cout << "n " << n << endl;
  if(T[n] != -1) return T[n];
  int tmp = 0;
  if(0 <= n-1 && V[n-1] > V[n]) tmp = max(tmp,rec(n-1));
  if(n+1 < N && V[n] < V[n+1]) tmp = max(tmp,rec(n+1));
  tmp += L-V[n];
  return T[n] = tmp;
}

int main(){

  cin >> N >> L;
  V.resize(N);

  for(int i = 0; i < N; i++) cin >> V[i];
  memset(T,-1,sizeof(T));

  int ans = 0;
  for(int i = 0; i < N; i++) ans = max(ans,rec(i));
  cout << ans << endl;
  return 0;
}

