ll now=0;
void jud(ll x){
	if(x<0||n<=x||fallen[x]) return;
	if((x-1>0&&!ko[x-1]&&a[x-1]<a[x])||(x+1<n&&!ko[x+1]&&a[x+1]<a[x])) return;
	q.push(P(a[x]+now,x));
	fallen[x]=1;
	return;
}
