signed main()
{
	priority_queue<P,vector<P>,greater<P> >que;
	cin >> n >> l;
	a[0] = a[n+1] = 0;
	for(int i = 0;i < n;++i)
	{
		cin >> a[i + 1];
	}
	for(int i = 1;i <= n;++i)
	{
		if(a[i] > a[i - 1] && a[i] > a[i + 1])
		{
			que.push(make_pair(l - a[i],i));
		}
	}
	Int ans = 0;
	while(!que.empty())
	{
		P p = que.top();que.pop();
		Int time = p.first;
