#include<vector>
using namespace std;
typedef long long ll;
const int N  = 100010;
bool vis[N];
int in[N];
ll dp[N];

void tsort(int n,int now,int *in,vector<int> &a){
  if (vis[now])return;
  vis[now] = true;
  if (now-1 >= 0             && in[now] > in[now-1])tsort(n,now-1,in,a);
  if (now+1 != n && in[now] > in[now+1])tsort(n,now+1,in,a);
  a.push_back(now);
}

main(){
  int n;
  ll l;
  while(cin>>n>>l && n){
    for(int i=0;i<n;i++)cin>>in[i],vis[i]=false;
    vector<int> a;
    for(int i=0;i<n;i++)if (!vis[i])tsort(n,i,in,a);
    reverse(a.begin(),a.end());
    ll ans = 0;
    for(int i=0;i<n;i++){
      int now = a[i];
      ll t = 0;
      if (now != 0   && in[now-1] > in[now])t = max(t,dp[now-1]);
      if (now != n-1 && in[now+1] > in[now])t = max(t,dp[now+1]);
      dp[now] = t+l-in[now];
      ans = max(ans,dp[now]);
    }
    cout << ans << endl;
  }
}

