	int curId;

	for (int i = 0; i < N; ++i) {
		curId = IciclesInfo[i].second;
		if (DpTime[curId - 1] == 0 && DpTime[curId + 1] == 0) {
			DpTime[curId] = L - IciclesInfo[i].first;
			continue;
		}
		if (DpTime[curId - 1] > DpTime[curId + 1]) {
			DpTime[curId] = DpTime[curId - 1] + L - IciclesInfo[i].first;
		}
		else {
			DpTime[curId] = DpTime[curId + 1] + L - IciclesInfo[i].first;
		}
	}
}

