int solve(int i){
  if(dp[i]) return dp[i];
  int ret = 0;
  if(a[i-1] > a[i])ret = solve(i-1);
  if(a[i+1] > a[i])ret = max(ret,solve(i+1));
  return dp[i] = ret + l - a[i];
}

