int time[100002];
int data[100002];

int main(){

	for(int i = 0 ; i < 100002 ; i++)
		time[i] = -1;
	int n,L;
	cin >> n >> L;

	time[0] = time[n+1] = data[n+1] = data[0] = 0 ;

	priority_queue< pair<int,int> > Q;
	for(int i = 1 ; i <= n ; i++){
		cin >> data[i];
		Q.push(make_pair(data[i],i));
	}
	int s = 0;
	while(Q.size()){

		int len = Q.top().first , index = Q.top().second ; Q.pop();
		if( data[index-1] < data[index] && data[index+1] < data[index]){
			len = min(L , len+s);
		}else if(~time[index-1] && ~time[index+1]){
			len = min(L , (s-max(time[index+1],time[index-1]) + len) );
		}else if(~time[index-1]){
			len = min(L , (s-time[index-1]) + len);
		}else if(~time[index+1]){
			len = min(L , (s-time[index+1]) + len);
		}else{
			assert(0);
		}
		int usetime = L - len;
		s += usetime;
		time[index] = s;
	}
	cout << s << endl;
}

#include <bits/stdc++.h>
#define INF 1e+10
#define f first
#define s second
using namespace std;
typedef long long ll;
typedef pair<ll,ll> P;
typedef pair<P,ll> PP;

int a[200000],n,m;
P dist[200000];

int main(){
  cin>>n>>m;
  for(int i=1;i<=n;i++) cin>>a[i];
  for(int i=0;i<=n+1;i++) dist[i]=P(INF,INF);
  priority_queue < PP, vector< PP >, greater < PP > > Q;
  for(int i=1;i<=n;i++) if(a[i]>a[i-1] && a[i]>a[i+1]) Q.push(PP(P(a[i],0),i));
  while(!Q.empty()){
    ll cost1 = Q.top().f.f;
    ll cost2 = Q.top().f.s;
    ll p=Q.top().second;
    Q.pop();
    cost2 += m-a[p];
    if(cost1>dist[p].f||cost1<a[p]) continue;
    if(cost2<dist[p].s && cost1==dist[p].f && dist[p].s!=INF) continue;
    dist[p]=P(cost1,cost2);
    if(p-1>0) Q.push(PP(P(a[p],cost2),p-1));
    if(p+1<=n) Q.push(PP(P(a[p],cost2),p+1));
  }
  ll ans=-1;
  for(int i=1;i<=n;i++) ans = max(ans,dist[i].second);
  cout<<ans<<endl;

  return 0;
}

