#include <bits/stdc++.h>

using namespace std;

int N, L;
int a[100001];
int dp[1000001];

int solve(int idx)
{
  int ret = 0;
  if(idx > 0 && a[idx - 1] > a[idx]) ret = max(ret, solve(idx - 1));
  if(idx < N-1 && a[idx + 1] > a[idx]) ret = max(ret, solve(idx + 1));
  ret += L - a[idx];
  return dp[idx] = ret;
}

