                if(t<m-s[i])t=m-s[i];
                temp[i]=s[i];

            }
        }
        FOR(i,1,n){
            if(temp[i])s[i]=0;
        }
        time+=t;
        FOR(i,1,n){
            //cout<<s[i]<<" "<<i<<endl;
            if(s[i]&&s[i+1]<s[i]&&s[i-1]<s[i]){
                s[i]+=t-(m-temp[i+1]);
                if(s[i]>m)s[i]=m;
            }
        }
    }
    cout<<time<<endl;
    return 0;
}

