    else if(left==n-1){
        if(a[left]>a[left-1]){
            pq.push(P(a[left]-diff,left));
            used[left]=true;
        }
    }
    else{
        if(a[left]>a[left-1]&&a[left]>a[left+1]){
            used[left]=true;
            pq.push(P(a[left]-diff,left));
        }
    }
}

