using namespace std;


int MAX_N = 100005;


int main(int argc, char const *argv[]) {
  int n, l;
  long long max_time = 0;
  vector<int> icicles(MAX_N, 0);
  vector<int> valleys;

  scanf("%d %d", &n, &l);
  for (int i = 1; i <= n; i++) {
    int ai;
    scanf("%d", &ai);
    icicles[i] = ai;
  }

  valleys.push_back(1);
  for (int i = 2; i <= n - 1; i++) {
    if (icicles[i] < icicles[i - 1] && icicles[i] < icicles[i + 1]) {
      valleys.push_back(i);
    }
  }
  valleys.push_back(n);

  for (auto& pos: valleys) {
    auto left_time = l - icicles[pos], right_time = l - icicles[pos];
    auto left_peak = pos;
    while (icicles[left_peak - 1] > icicles[left_peak]){
      left_time += l - icicles[--left_peak];
    }
    auto right_peak = pos;
    while(icicles[right_peak + 1] > icicles[right_peak]){
      right_time += l - icicles[++right_peak];
    }
    if (left_time > max_time) max_time = left_time;
    if (right_time > max_time) max_time = right_time;
  }
  printf("%lld\n", max_time);
  return 0;
}

