int rec(int n){
	if(memo[n]!=-1)
		return memo[n];

	if(d[n] > d[n-1] && d[n] > d[n+1]){
		return memo[n]=L-d[n];
	}else if(d[n] <= d[n-1] && d[n] > d[n+1] ){
		return memo[n]=L-d[n]+rec(n-1);
	}else if(d[n] > d[n-1] && d[n] <= d[n+1] ){
		return memo[n]=L-d[n]+rec(n+1);
	}else if(d[n] <= d[n-1] && d[n] <= d[n+1] ){
		return memo[n]=L-d[n]+max(rec(n-1), rec(n+1));
	}
