int a[100002], N, L;
int dp[100002]; // おれる時間

int rec(int idx){
  if(~dp[idx]) return(dp[idx]);
  int ret = 0;
  if(a[idx - 1] >= a[idx]) ret = max( ret, rec(idx - 1));
  if(a[idx + 1] >= a[idx]) ret = max( ret, rec(idx + 1));
  return(dp[idx] = ret + L - a[idx]);
}

