	for(int i = 0;i < n;i++) cin >> a[i];
	for(int i = 0;i < n;i++){
		if((i == 0 && a[i] > a[i + 1]) || (i == n - 1 && a[i] > a[i - 1]) || (a[i] > a[i - 1] && a[i] > a[i + 1])) que.push(i);
	}
	while(!que.empty()){
		int num = que.front();que.pop();
		if(time[num] != 0) continue;
		if(!((num == 0 && a[num] > a[num + 1]) || (num == n - 1 && a[num] > a[num - 1]) || (a[num] > a[num - 1] && a[num] > a[num + 1]))) continue;
		if(num == 0) {
			time[num] = time[num + 1] + l - a[num];
			que.push(num + 1);
		}
		else if(num == n - 1) {
			time[num] = time[num - 1] + l - a[num];
			que.push(num - 1);
		}
		else {
			time[num] = max(time[num - 1],time[num + 1]) + l - a[num];
			que.push(num - 1);
