int dfs(int v) {
  int ret = 0;
  int s = L - A[v - 1];
  int l = L - A[v + 1];
  if(v > 1) {
    if(A[v - 1] > A[v - 2]) {
      A[v - 1] = 0;
      ret = dfs(v - 1) + s;
    }
  }
  if(v < N) {
    if(A[v + 1] > A[v + 2]) {
      A[v + 1] = 0;
      ret = max(ret, dfs(v + 1) + l);
    }
  }
  return ret;
} 

