ll check(ll num) {
  if (memo[num] > 0) return memo[num];
  ll left = num != 0  && ai[num] < ai[num - 1] ? check(num - 1) : 0;
  ll right = num != n - 1 && ai[num] < ai[num + 1]  ? check(num + 1) : 0;
  return memo[num] = max(left, right) + l - ai[num];
}

