const int MOD = 1e9 + 7;

bool ready(int i, vector<pii> &icicle) {
    int N = icicle.size();
    if(i < 0 || i >= N)
        return false;
    if(i == 0) {
        if(icicle[i].first > icicle[i + 1].first) {
            return true;
        }
    } else if(i == N - 1) {
        if(icicle[i].first > icicle[i - 1].first) {
            return true;
        }
    } else {
        if(icicle[i].first > icicle[i + 1].first &&
           icicle[i].first > icicle[i - 1].first) {
            return true;
        }
    }
    return false;
}

int main() {
    int N, L;
    cin >> N >> L;
    vector<pii> icicle(N);
    rep(i, N) {
        int a;
        cin >> a;
        icicle[i] = MP(a, 0);
    }
    queue<pii> que;
    rep(i, N) {
        if(ready(i, icicle)) {
            que.push(MP(i, 0));
        }
    }

    while(!que.empty()) {
        auto p = que.front();
        que.pop();
        int idx = p.first;
        int now = p.second;
        icicle[idx].second = now + (L - icicle[idx].first);
        icicle[idx].first = 0;
        if(ready(idx - 1, icicle)) {
            int ma = 0;
            if(idx - 2 >= 0)
                ma = icicle[idx - 2].second;
            que.push(MP(idx - 1, max(ma, icicle[idx].second)));
        }
        if(ready(idx + 1, icicle)) {
            int ma = 0;
            if(idx + 2 < N)
                ma = icicle[idx + 2].second;
            que.push(MP(idx + 1, max(ma, icicle[idx].second)));
        }
    }
    int res = 0;
    rep(i, N) { res = max(res, icicle[i].second); }
    cout << res << endl;
    return 0;
}

