using namespace std;

typedef long long ll;

const int N = 100000;
const ll M = ((ll)1<<40);

ll n, l;
ll num[N], m[N];

bool isBig(int current, ll t){ // current is bigger: true
  if(current == 0 || current == n - 1) return 0;
  return num[current] > t;
}

ll solve(int current, int dir){
  //cout << "current = " << current << " m = " << m[current] << endl;
  if(current < 0 || current >= n) return 0;
  if(m[current] < M) return m[current];

  if((current == 0 || num[current] > num[current - 1]) &&
     (current == n - 1 || num[current] > num[current + 1])){
    m[current] = l - num[current];
  }else if(dir == 0){ // first
    if(isBig(current - 1, num[current]) && isBig(current + 1, num[current]))
      m[current] = max(solve(current - 1, 1), solve(current + 1, 2)) + l - num[current];
    else if(isBig(current - 1, num[current]))
      m[current] = solve(current - 1, 1) + l - num[current];
    else
      m[current] = solve(current + 1, 2) + l - num[current];
  }else if(dir == 1){ // from right
    m[current] = solve(current - 1, 1) + l - num[current];
  }else{ // from left
    m[current] = solve(current + 1, 2) + l - num[current];
  }
  return m[current];
}

int main(){
  while(cin >> n >> l && n|l){
    for(int i = 0; i < (int)n; i++){
      cin >> num[i];
      m[i] = M;
    }

    ll ans = 0;

    for(int i = 0; i < (int)n; i++){
      if(m[i] == M) solve(i, 0);
      ans = max(ans, m[i]);
    }

    cout << ans << endl;
  }
  return 0;
}

