					if (j != i){
						if (j == pos&&pos != 0){
							sum[j] += max(sum[j + 1], sum[j - 1]);
						}
						else sum[j] += sum[j + 1];
						if (j == pos)ans = max(ans, sum[j]);
					}
				}
			}
		}
		else{
			if (a[i] > a[i + 1]){
				for (int j = i; j >= pos; j--){
					sum[j] = l - a[j];
					if (j != i){
						if (j == pos&&pos != 0){
							sum[j] += max(sum[j + 1], sum[j - 1]);
						}
						else sum[j] += sum[j + 1];
						if (j == pos)ans = max(ans, sum[j]);
					}
				}
				pos = i + 1;
			}
		}
	}
	cout << ans << endl;
}

