using namespace std;
typedef long long int LL;
typedef pair<LL, int> PLI;
typedef vector<PLI> VP;

int n, l, a[100010];
bool growing[100010];
priority_queue<PLI, VP, greater<PLI> > Q;
LL nowtime = 0;

int main()
{
  scanf("%d%d", &n, &l);
  for(int i = 1; i <= n; i++)
    scanf("%d", &a[i]);

  for(int i = 1; i <= n; i++)
    if(a[i] > a[i - 1] && a[i] > a[i + 1]){
      growing[i] = true;
      Q.push(MP(l - a[i], i));
    }

  while(!Q.empty()){
    PLI breaked = Q.top();
    Q.pop();
    nowtime = breaked.first;
    a[breaked.second] = 0;

    if(breaked.second > 1 && !growing[breaked.second - 1] && a[breaked.second - 1] > a[breaked.second - 2]){
      growing[breaked.second - 1] = true;
      Q.push(MP((LL)l - a[breaked.second - 1] + nowtime, breaked.second - 1));
    }

    if(breaked.second < n && !growing[breaked.second + 1] && a[breaked.second + 1] > a[breaked.second + 2]){
      growing[breaked.second + 1] = true;
      Q.push(MP((LL)l - a[breaked.second + 1] + nowtime, breaked.second + 1));
    }
  }

  printf("%lld\n", nowtime);
  return 0;
}

