		if (i == 0) {
			if (length[i + 1] > length[i])pq.push(P(length[i], i));
		}
		else if(i == M - 1) {
			if (length[i] < length[i - 1]) {
				pq.push(P(length[i], i));
			}
		}
		else {
			if (length[i] < length[i - 1] && length[i + 1] > length[i]) {
				pq.push(P(length[i], i));
			}
		}
	}
