using namespace std;
struct P {
	long long place, time;
};
bool operator<(P a, P b) {
	return a.time < b.time;
}
bool operator>(P a, P b) {
	return a.time > b.time;
}
long long a[100000];
int main() {
	long long n, l;
	while (cin >> n >> l) {
		priority_queue<P, vector<P>, greater<P>>p;
		for (int i = 0; i < n; i++) {
			int howlong;
			cin >> howlong;
			a[i] = howlong;
		}
		for (int i = 0; i < n; i++) {
			if (i == 0) {
				if (a[1] < a[0]) p.push(P{ i,l - a[0] });
			}
			else if (i == n - 1) {
				if (a[n - 1] > a[n - 2]) p.push(P{ i,l - a[n-1] });
			}
			else {
				if (a[i] > a[i - 1] && a[i] > a[i + 1]) p.push(P{ i,l - a[i] });
			}
		}
		long long sum = 0;
		while (!p.empty()) {
			P t = p.top(); p.pop();
			if (a[t.place] != 0) {
				a[t.place] = 0;
				sum = max(sum, t.time);
				long long i = t.place-1;
				if (i == 0) {
					if (a[1] < a[0]) p.push(P{ i,t.time+(l - a[0]) });
				}
				else if (i == n - 1) {
					if (a[n - 1] > a[n - 2]) p.push(P{ i,t.time+(l - a[n - 1]) });
				}
				else if(i>=0&&i<n){
					if (a[i] > a[i - 1] && a[i] > a[i + 1]) p.push(P{ i,t.time+(l - a[i]) });
				}
				i += 2;
				if (i == 0) {
					if (a[1] < a[0]) p.push(P{ i,t.time+(l - a[0] )});
				}
				else if (i == n - 1) {
					if (a[n - 1] > a[n - 2]) p.push(P{ i,t.time+(l - a[n - 1] )});
				}
				else if(i<n&&i>=0){
					if (a[i] > a[i - 1] && a[i] > a[i + 1]) p.push(P{ i,t.time+(l - a[i] )});
				}
			}
		}
		cout << sum << endl;
	}
}

