    val[i] = data[0][i];
  }
  int ans = 0;
  int rem = n;
  int now = 0;
  int next = 1;
  while(rem > 0){
    sort(val, val + n);
    int valm = l - val[n-1];
    if(data[now][0] > data[now][1]){
      data[next][0] = data[now][0] + valm;
      if(data[next][0] >= l){
        data[next][0] = 0;
        --rem;
      }
    }else{
      data[next][0] = data[now][0];
    }
    val[0] = data[next][0];
    for(int i=1;i<n-1;i++){
      if(data[now][i-1] < data[now][i] && data[now][i] > data[now][i+1]){
        data[next][i] = data[now][i] + valm;
        if(data[next][i] >= l){
          data[next][i] = 0;
          --rem;
        }
      }else{
        data[next][i] = data[now][i];
      }
      val[i] = data[next][i];
    }
    if(data[now][n-2] < data[now][n-1]){
