	{
		p.push_back(n);
	}
	while(!p.empty())
	{
		//cout << "BB" ;
		a=p[p.size()-1];
		p.pop_back();
		if(ice[a-1]<ice[a] && ice[a]>ice[a+1])
		{
			tim[a]=m-ice[a];
			if(ice[a-1]!=0) p.push_back(a-1);
			if(ice[a+1]!=0)p.push_back(a+1);
		}
		else if(ice[a-1]<ice[a] || ice[a]>ice[a+1])
		{
			if(ice[a-1]>ice[a])
			{
				f=tim[a-1];
				if(ice[a+1]!=0) p.push_back(a+1);
			}
			if(ice[a+1]>ice[a])
			{
				f=tim[a+1];
				if(ice[a-1]) p.push_back(a-1);
			}
			tim[a]=m-ice[a]+f;
		}
		else
		{
			if(tim[a-1]>tim[a+1])
			{
				tim[a]=m-ice[a]+tim[a-1];
			}
			if(tim[a-1]<tim[a+1])
			{
				tim[a]=m-ice[a]+tim[a+1];
			}
		}
	}
	//cout << "CC";
	long mx=0;
	for(i=1;i<=n;i++)
	{
		if(mx<tim[i]) mx=tim[i];
	}
	cout << mx;
	return 0;
}

