#include <iostream>
#include <cstdio>
#include <algorithm>
#include <queue>
#include <utility>
#include <tuple>
using namespace std;
using AnsType = long long int;

const int dx[] = {-1, 1};
AnsType N, L;

struct Elem {
  AnsType idx, h, t;
  Elem() {}
  Elem(AnsType a, AnsType b, AnsType c) : idx(a), h(b), t(c) {}
  bool operator<(const Elem &e) const {
    AnsType vl = (L -   h) +   t;
    AnsType vr = (L - e.h) + e.t;
    return vl > vr;
  }
};

bool is_valid_idx(int x) {
  if(x == 0 and x == N+1) return false;
  return true;
}

int main() {
  cin >> N >> L;
  vector<AnsType> H(N+2);
  for(int i=1; i<=N; i++) {
    cin >> H[i];
  }

  priority_queue<Elem> que;
  for(int i=1; i<=N; i++) {
    if(H[i-1] < H[i] and H[i+1] < H[i]) {
      que.emplace(i, H[i], 0);
    }
  }

  AnsType ans = 0;
  while(que.size()) {
    Elem cur = que.top(); que.pop();
    AnsType idx = cur.idx, h = cur.h, t = cur.t;
    AnsType tend = (L - h) + t;
    ans = max(ans, tend);
    H[idx] = 0;

    for(int k=0; k<2; k++) {
      AnsType nidx = idx + dx[k];
      if(!is_valid_idx(nidx)) continue;
      if(H[nidx-1] < H[nidx] and H[nidx+1] < H[nidx]) {
        que.emplace(nidx, H[nidx], tend);
      }
    }
  }
  cout << ans << endl;
  return 0;
}

