int getInt(){
  int ret = 0,c;
  c = getchar();
  while(!isdigit(c)) c = getchar();
  while(isdigit(c)){
    ret *= 10;
    ret += c - '0';
    c = getchar();
  }
  return ret;
}

int main(){
  int n = getInt();
  int l = getInt();

  vector<int> a(n);

  REP(i,n) a[i] = getInt();

  typedef pair<int,int> task;
  priority_queue<task,vector<task>,greater<task> > pq;

  REP(i,n){
    if(i == 0){ if(a[0] > a[1]) pq.push(mp(l - a[i], i)); }
    else if(i == n - 1){ if(a[n-1] > a[n-2]) pq.push(mp(l - a[i], i)); }
    else if(a[i-1] < a[i] && a[i+1] < a[i]) pq.push(mp(l - a[i], i));
  }

  int ans = 0;
  while(pq.size()){
    task t = pq.top(); pq.pop();
    int tm = t.f; ans = tm;
    int nn = t.s;

    a[nn] = 0;

    if(nn != 0){
      int i = nn - 1;
      if(a[i] > 0){
        if(i == 0){ if(a[0] > a[1]) pq.push(mp(tm + l - a[i], i)); }
        else if(i == n - 1){ if(a[n-1] > a[n-2]) pq.push(mp(tm + l - a[i], i)); }
        else if(a[i-1] < a[i] && a[i+1] < a[i]) pq.push(mp(tm + l - a[i], i));
      }
    }

    if(nn != n - 1){
      int i = nn + 1;
      if(a[i] > 0){
        if(i == 0){ if(a[0] > a[1]) pq.push(mp(tm + l - a[i], i)); }
        else if(i == n - 1){ if(a[n-1] > a[n-2]) pq.push(mp(tm + l - a[i], i)); }
        else if(a[i-1] < a[i] && a[i+1] < a[i]) pq.push(mp(tm + l - a[i], i));
      }
    }
  }

  print(ans);

  return 0;
}

