const int MAX_N = 100005;

int main() {
    int n, l;
    unsigned long long max_time = 0;
    vector<int> lens(MAX_N, 0);
    vector<int> valleys;

    cin >> n >> l;
    for (int i = 1; i <= n; i++) { cin >> lens[i]; }

    valleys.push_back(1);
    for (int i = 2; i <= n - 1; i++) {
        if (lens[i] < lens[i - 1] && lens[i] < lens[i + 1]) {
            valleys.push_back(i);
        }
    }
    valleys.push_back(n);

    for (auto& pos: valleys) {
        auto left_pos = pos - 1, right_pos = pos + 1;
        auto left_time = l - lens[pos], right_time = l - lens[pos];
        auto now_pos = pos;
        while (lens[now_pos - 1] > lens[now_pos]) {
            left_time += l - lens[--now_pos];
        }
        now_pos = pos;
        while (lens[now_pos + 1] > lens[now_pos]) {
            right_time += l - lens[++right_pos];
        }
        if (left_time > max_time) { max_time = left_time; }
        if (right_time > max_time) { max_time = right_time; }
    }

    printf("%llu\n", max_time);

    return 0;
}

