vi v;

bool check(int i, int n){
    if(n <= i) return false;
    if(i == 0){if(v[i] > v[i+1]) return true;}
    else if(i == n-1){if(v[i] > v[i-1]) return true;}
    else if(v[i] > v[i-1] && v[i] > v[i+1]) return true;
    return false;
}

int main(){
    int n, l, t = 0;
    cin >> n >> l;
    rep(i, n){int t; cin >> t; v.pb(t);}
    priority_queue<pair<int, int> > q;
    rep(i, n) if(check(i, n)) q.push(mp(v[i], i));
    int count = 0;
    while(count != v.size()){
        pair<int, int> m = q.top(); q.pop();
        v[m.second] = 0;
        int d = l-m.first-t;
        if(d > 0) t+=d;

        if(check(m.second-1, n)){v[m.second-1] -= t; q.push(mp(v[m.second-1], m.second-1));}
        if(check(m.second+1, n)){v[m.second+1] -= t; q.push(mp(v[m.second+1], m.second+1));}
        count++;
    }
    cout << t << endl;
}

