int main(){
  int total_icicles,limit_length;
  while(~scanf("%d %d",&total_icicles,&limit_length)){
    int icicles[100000];
    for(int icicle_idx = 0; icicle_idx < total_icicles; icicle_idx++){
      scanf("%d",icicles + icicle_idx);
    }

    int res = 0;
    for(int turn=0;turn<total_icicles;turn++){
      bool target[100000];
      memset(target,false,sizeof(target));
      int length = INF;
      for(int icicle_idx=0;icicle_idx<total_icicles;icicle_idx++){
	if(icicles[icicle_idx] == 0) continue;
	int lhs = icicle_idx - 1;
