	if(lhs >= 0){
	  if(icicles[lhs] > icicles[icicle_idx]){
	    isok = false;
	  }
	}
	if(rhs < total_icicles){
	  if(icicles[rhs] > icicles[icicle_idx]){
	    isok = false;
	  }
	}

	if(isok){
	  length = min(limit_length - icicles[icicle_idx],length);
	  target[icicle_idx] = true;
	}
      }
      if(length == INF) break;
      for(int icicle_idx = 0;icicle_idx<total_icicles; icicle_idx++){
	if(target[icicle_idx]){
	  icicles[icicle_idx] += length;
	  if(icicles[icicle_idx] == limit_length){
	    icicles[icicle_idx] = 0;
	  }
	}
      }
      res += length;
    }
    printf("%d\n",res);
  }
}

