#include<bits/stdc++.h>
using namespace std;

using i64 = long long int;

struct Icicle{
    i64 length, idx;
    bool operator<(const Icicle &obj)const{
        if(length == obj.length)return idx < obj.idx;
        return length < obj.length;
    }
};

bool can_grow(vector<i64> &v, i64 idx, i64 n){
    if(idx < 0 || idx >= n)return false;
    if(idx == 0 && v[idx] > v[idx+1])return true;
    if(idx == n-1 && v[idx] > v[idx-1])return true;
    if(v[idx] > v[idx-1] && v[idx] > v[idx+1])return true;
    return false;
}

int main(){
    i64 n, l;
    cin >> n >> l;
    vector<i64> v;
    for(i64 i=0;i<n;++i){
        i64 a;
        cin >> a;
        v.push_back(a);
    }

    priority_queue<Icicle> que;
    for(i64 i=0;i<n;++i){
        if(can_grow(v, i, n))que.push((Icicle){v[i], i});
    }

    i64 t = 0;
    while(!que.empty()){
        Icicle icicle = que.top(); que.pop();
        t = l - icicle.length;
        i64 idx = icicle.idx;
        v[idx] = 0;
        if(can_grow(v, idx-1, n))que.push((Icicle){v[idx-1] - t, idx-1});
        if(can_grow(v, idx+1, n))que.push((Icicle){v[idx+1] - t, idx+1});
    }

    cout << t << endl;

    return 0;
}

