using namespace std;
void reader(int *x) {
	int k = getchar_unlocked(); *x = k - '0';
	while (1) {
		k = getchar_unlocked();
		if (k < '0' || k > '9') break;
		*x = ((*x) << 1) + ((*x) << 3) + k - '0';
	}
}
int N, L, a[100009], dp[100009]; vector<int> x[50009];
int main() {
	reader(&N); reader(&L);
	for (int i = 0; i < N; i++) {
		reader(&a[i]);
		x[a[i]].push_back(i + 1);
	}
	for (int i = L - 1; i >= 1; i--) {
		for (int j : x[i]) {
			dp[j] = max(dp[j - 1], dp[j + 1]) + (L - a[j - 1]);
		}
	}
	printf("%d\n", *max_element(dp, dp + N + 1));
	return 0;
}

