int n, l, icicles[100001], time1, time2, roop, ans = 0;
int main(){
    cin >> n >> l;
    for(int i = 1; i <= n; i++){
        cin >> icicles[i];
    }

    for(int i = 1; i <= n; i++){
        if(icicles[i] > icicles[i - 1] && icicles[i] > icicles[i + 1]){
            time1 = l - icicles[i];
            time2 = l - icicles[i];

            roop = i;
            while(icicles[roop] > icicles[--roop] && roop > 0){
                time1 += l - icicles[roop];
            }
            ans = max(ans, time1);

            roop = i;
            while(icicles[roop] > icicles[++roop] && roop <= n){
                time2 += l - icicles[roop];
            }
            ans = max(ans, time2);
        }
    }

    cout << ans << endl;

    return 0;
}

