int N, L;
int Is[MAX_N + 2];
int Tm[MAX_N + 2];

int search ( int i )
{
	if (Tm[i] != NF) {
		return Tm[i];
	}
	int mx = 0;
	if (Is[i+1] > Is[i]) {
		mx = max(mx, search(i+1));
	}
	if (Is[i-1] > Is[i]) {
		mx = max(mx, search(i-1));
	}
	return Tm[i] = L - Is[i] + mx;
}

int main ( void )
{
	while (cin >> N >> L) {
		fill(Is, Is+MAX_N+2, 0);
		fill(Tm, Tm+MAX_N+2, NF);
		for (int i = 0; i < N; ++i) {
			cin >> Is[i+1];
		}

		int mx = 0;
		for (int i = 0; i < N; ++i) {
			mx = max(mx, search(i+1));
		}

		cout << mx << endl;
	}

	return 0;
}

