#define INF 10000000000

typedef pair<long long int, long long int> P;

int main() {
	long long int n, l;
	cin >> n >> l;
	vector<long long int> d(n);
	for (int i = 0; i < n; i++) {
		cin >> d[i];
	}
	vector<long long int> t(n, INF);
	priority_queue<P, vector<P>, greater<P> > pq;
	for (int i = 0; i < n; i++) {
		if (i == 0) {
			if (d[i] > d[i+1]) {
				pq.push(P(l-d[i], i));
			}
		} else if (i == n-1) {
			if (d[i] > d[i-1]) {
				pq.push(P(l-d[i], i));
			}
		} else {
			if (d[i] > d[i+1] && d[i] > d[i-1]) {
				pq.push(P(l-d[i], i));
			}
		}
	}
	long long int tt = 0;
	while (!pq.empty()) {
		P pp = pq.top();
		pq.pop();
		long long int a, b;
		a = pp.first;
		b = pp.second;
		t[b] = a;
		tt = max(tt, a);
		if (b == 0) {
			if (d[b+1] < d[b]) {
				if (b+1 == n-1) {
					pq.push(P(a+(l-d[b+1]), b+1));
				} else {
					if (d[b+2] > d[b+1]) {
						if (t[b+2] != INF) {
							pq.push(P(max(a, t[b+2])+(l-d[b+1]), b+1));
						}
					} else {
						pq.push(P(a+(l-d[b+1]), b+1));
					}
				}
			}
		} else if (b == n-1) {
			if (d[b-1] < d[b]) {
				if (b-1 == 0) {
					pq.push(P(a+(l-d[b-1]), b-1));
				} else {
					if (d[b-2] > d[b-1]) {
						if (t[b-2] != INF) {
							pq.push(P(max(a, t[b-2])+(l-d[b-1]), b-1));
						}
					} else {
						pq.push(P(a+(l-d[b-1]), b-1));
					}
				}
			}
		} else {
			if (d[b+1] < d[b]) {
				if (b+1 == n-1) {
					pq.push(P(a+(l-d[b+1]), b+1));
				} else{
					if (d[b+2] > d[b+1]) {
						if (t[b+2] != INF) {
							pq.push(P(max(a, t[b+2])+(l-d[b+1]), b+1));
						}
					} else {
						pq.push(P(a+(l-d[b+1]), b+1));
					}
				}
			}
			if (d[b-1] < d[b]) {
				if (b-1 == 0) {
					pq.push(P(a+(l-d[b-1]), b-1));
				} else {
					if (d[b-2] > d[b-1]) {
						if (t[b-2] != INF) {
							pq.push(P(max(a, t[b-2])+(l-d[b-1]), b-1));
						}
					} else {
						pq.push(P(a+(l-d[b-1]), b-1));
					}
				}
			}
		}
	}
	cout << tt << endl;
	return 0;
}

