        scanf("%d", &I[i]);
    }
    priority_queue< P, vector<P> > Q;
    for (int i = 0; i < N; i++) {
        if ( (i == 0 && I[i] > I[i+1]) || 
             (i == N-1 && I[i] > I[i-1]) ||
             (I[i] > I[i+1] && I[i] > I[i-1]) ) {
