#include <iostream>
#include <cstdio>
#include <algorithm>
using namespace std;

#define FOR(i,a,b) for(int i=(a);i<(b);++i)
#define REP(i,n) FOR(i,0,n)

int n, l;
int h[100002];
int dp[100002];

int solve(int pos) {
	if (dp[pos] > 0) return dp[pos];
	if (pos == 0 || pos == n - 2) return 0;

	int t = l - h[pos];

	if (h[pos - 1] < h[pos] && h[pos] > h[pos + 1])
		return dp[pos] = t;

	if (h[pos - 1] > h[pos])
		dp[pos] = max(dp[pos], solve(pos - 1) + t);
	if (h[pos + 1] > h[pos])
		dp[pos] = max(dp[pos], solve(pos + 1) + t);

	return dp[pos];
}

int main() {
	cin >> n >> l;
	h[0] = 0;
	REP(i, n) scanf("%d", &h[i + 1]);
	h[n + 1] = 0;
	n += 2;

	int ans = 0;
	FOR(i, 1, n - 1) ans = max(ans, solve(i));

	cout << ans << endl;

	return 0;
}

