int N,L,a[100002]={0};
int memo[100002];
int rec(int id)
{
  if(~memo[id])return memo[id];

  int wait=0;
  if(a[id-1]>a[id])wait=max(wait,rec(id-1));
  if(a[id]<a[id+1])wait=max(wait,rec(id+1));
  return memo[id] = wait + L-a[id];
}

int main()
{
  memset(memo,-1,sizeof(memo));
  cin>>N>>L;
  for(int i=1;i<=N;i++)cin>>a[i];
  int ans=0;
  for(int i=1;i<=N;i++)ans=max(ans,rec(i));
  cout<<ans<<endl;
}

#include <bits/stdc++.h>

using namespace std;

typedef pair<int, int> pii;

#define MP make_pair

int a[100000];
int n, l;

bool is_valid(int i)
{
    if (i == 0) return a[i] > a[i + 1];
    if (i == n - 1) return a[i] > a[i - 1];

    return a[i] > a[i - 1] && a[i] > a[i + 1];
}

int main()
{
    cin >> n >> l;

    for (int i = 0; i < n; ++i) cin >> a[i];

    vector<pii> v;

    for (int i = 0; i < n; ++i) if (is_valid(i)) v.push_back(MP(a[i], i));

    int ans = 0;

    while (v.size()) {
        sort(v.begin(), v.end());

        int diff = l - v.back().first;

        vector<pii> b;

        for (int i = v.size() - 1; i >= 0; --i) {
            int &fst = v[i].first, &scd = v[i].second;

            if (fst + diff == l) {
                a[scd] = 0;
                v.pop_back();

                if (scd - 1 >= 0 && is_valid(scd - 1)) b.push_back(MP(a[scd - 1], scd - 1));
                if (scd + 1 < n  && is_valid(scd + 1)) b.push_back(MP(a[scd + 1], scd + 1));
            } else {
                a[scd] += diff;
                fst += diff;
            }
        }

        for (int i = 0; i < b.size(); ++i) v.push_back(b[i]);

        ans += diff;
    }

    cout << ans << endl;
}

