using namespace std;

#define FOR(i,a,b) for(int i=(a);i<(b);++i)
#define REP(i,n) FOR(i,0,n)

typedef pair<int, int> pii;

int n, l;
int h[100002];
bool done[100002];

int main() {
	cin >> n >> l;
	h[0] = 0;
	REP(i, n) scanf("%d", &h[i + 1]);
	h[n + 1] = 0;
	n += 2;

	priority_queue<pii, vector<pii>, greater<pii>> q;	//hout, i
	FOR(i, 1, n - 1) if (h[i - 1] < h[i] && h[i] > h[i + 1])
		q.push(make_pair(l - h[i], i));

	int ans = 0;
	while (!q.empty()) {
		pii p = q.top(); q.pop();
		int pos = p.second;
		int t = p.first;

		if (done[pos]) continue;

		h[pos] = 0;
		done[pos] = true;
		ans = max(ans, t);

		if (pos - 2 >= 0 && h[pos - 2] < h[pos - 1] && h[pos - 1] > h[pos])
			q.push(make_pair(t + l - h[pos - 1], pos - 1));
		if (pos + 2 < n && h[pos] < h[pos + 1] && h[pos + 1] > h[pos + 2])
			q.push(make_pair(t + l - h[pos + 1], pos + 1));
	}

	cout << ans << endl;

	return 0;
}

