unsigned long tk[100000];
priority_queue<pii> ln;

int main(){
	fill(tk,tk+100000,0);
	int n;
	int l;
	scanf("%d %d",&n,&l);
	int ti =0;
	REP(i,n){
		scanf("%d",&ti);
		ln.push(MP(ti,i));
