	pii t;
	unsigned long mx=0;
	REP(i,n){
		t = ln.top();
		ln.pop();
		tk[t.second] += l-t.first;

		if(t.second>0)
			tk[t.second-1] = max(tk[t.second-1],tk[t.second]);

		if(t.second<l-1)
			tk[t.second+1] = max(tk[t.second+1],tk[t.second]);

		mx=max(mx,tk[t.second]);
	}

	printf("%d\n",mx);
	getchar();
	getchar();
}

#include <iostream>
#include <algorithm>
using namespace std;

int n, l, icicles[100001], time1, time2, loop, ans = 0;
int main(){
	cin >> n >> l;
	for(int i = 1; i <= n; i++){
		cin >> icicles[i];
	}

	for(int i = 1; i <= n; i++){
		if(icicles[i] > icicles[i - 1] && icicles[i] > icicles[i + 1]){
			time1 = l - icicles[i];
			time2 = l - icicles[i];

			loop = i;
			while(icicles[loop] > icicles[--loop] && loop > 0){
				time1 += l - icicles[loop];
			}
			ans = max(ans, time1);

			loop = i;
			while(icicles[loop] > icicles[++loop] && loop <= n){
				time2 += l - icicles[loop];
			}
			ans = max(ans, time2);
		}
	}

	cout << ans << endl;

	return 0;
}

