using namespace std;

const int INF = 100000000;

int n, l;
int data[100002];
int memo[100002];

int func(int i){
  if(memo[i] != -1) return memo[i];
  int res = 0;
  if(data[i+1] > data[i]) res = max(res, func(i+1));
  if(data[i-1] > data[i]) res = max(res, func(i-1));
  return memo[i] = res + l - data[i];
}


main(){
  fill(memo, memo+100002, -1);
  cin >> n >> l;
  for(int i=1;i<=n;++i){
    cin >> data[i];
  }
  int ans = 0;
  for(int i=1;i<=n;++i){
    ans = max(ans, func(i));
  }
  cout << ans << endl;
  return 0;
}

